mapper.select:
select

from at_table_config
where 1=1

	
		and table_name like concat('%',#{model.tableName},'%')
	
	
		and status = #{model.status}
	
	
		and ( pid = #{model.pId}
		or id = #{model.pId} )
	
	
		and grid_name like concat('%',#{model.gridName},'%')
	


	order by ${sort}

select

from at_table_config

	
		 pid = #{pId,jdbcType=VARCHAR}
	
	
		 status = #{status,jdbcType=VARCHAR}
	

order by seq
